// -----------------------------------------------------------------------------
// rnadom string test
function str_random($length)
{
    $str = "";
    srand((double)microtime()*1000000);
    $char_list = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
    $char_list .= "abcdefghijklmnopqrstuvwxyz";
    $char_list .= "1234567890";
    $char_list .= "<>!?[]%^&*()";
    for($i=0; $i<$length; ++$i)
        $str .= substr($char_list,(rand()%(strlen($char_list))), 1);
    return $str;
}

for($i=0; $i<60; ++$i) {
    $str = str_random($i);
    //echo $str."\n<br>";
    $dom->load($str, false);
    //echo $dom->save()."\n<br>";
    assert($dom==$str);
}

// -----------------------------------------------------------------------------
// lowercase test
$str = <<<HTML
<img class="class0" id="id0" src="src0">
HTML;
$dom->load($str);
assert(count($dom->find('img'))==1);
assert(count($dom->find('IMG'))==1);
assert(isset($dom->find('img', 0)->class));
assert(!isset($dom->find('img', 0)->CLASS));
assert($dom->find('img', 0)->class=='class0');
assert($dom==$str);
// -----------------------------------------------
$str = <<<HTML
<IMG CLASS="class0" ID="id0" SRC="src0">
HTML;
$dom->load($str);
assert(count($dom->find('img'))==1);
assert(count($dom->find('IMG'))==1);
assert(isset($dom->find('img', 0)->class));
assert(!isset($dom->find('img', 0)->CLASS));
assert($dom->find('img', 0)->class=='class0');
assert($dom==strtolower($str));
// -----------------------------------------------
$str = <<<HTML
<IMG CLASS="class0" ID="id0" SRC="src0">
HTML;
$dom->load($str, false);
assert(count($dom->find('img'))==0);
assert(count($dom->find('IMG'))==1);
assert(isset($dom->find('IMG', 0)->CLASS));
assert(!isset($dom->find('IMG', 0)->class));
assert($dom->find('IMG', 0)->CLASS=='class0');
assert($dom==$str);

// -----------------------------------------------------------------------------
// tear down
$dom->clear();
unset($dom);
?>