class Zend_Validate_Barcode_Issn extends Zend_Validate_Barcode_AdapterAbstract
{
    /**
     * Allowed barcode lengths
     * @var integer
     */
    protected $_length = array(8, 13);

    /**
     * Allowed barcode characters
     * @var string
     */
    protected $_characters = '0123456789X';

    /**
     * Checksum function
     * @var string
     */
